import React from 'react';

export default class RankingTable extends React.Component {
    constructor(props) {
		super(props);

        this.state = {
            pokemonList: [{
                id: 0,
                name: "Loading...",
                hp: 0,
                attack: 0,
                defense: 0,
                special_attack: 0,
                special_defense: 0,
                speed: 0
            }]
        };

        this.getRanking = this.getRanking.bind(this);
	}

    componentDidMount() {
		(async () => {
            this.getRanking(this.props.dbFormattedStats[0]);
		})();
	}

    getRanking(stat) {
        (async () => {
            let json = await fetch("/ranking", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({
                    stat: stat,
                })
            });
            let result = await json.json();

            this.setState({
                pokemonList: result
            });
        })();
    }

    getRankedList(stat, list) {
        let result = list.sort((a, b) => {
            return b[stat] - a[stat]
        });

        return result;
    }

    render() {
        let pokemonList = this.state.pokemonList;
        let stats = this.props.stats;

        return (
            <div className='ranking-parent'>
                <h1>Community Rankings</h1>
                <label for='stats'>Sort by:</label>
                <select name='stats' id='stat-dropdown'>
                    {stats.map((stat) => (
                        <option
                            key={stat}
                            value={stat}
                        >
                            {stat}
                        </option>
                    ))}
                </select>
                <table className='ranking-table'>
                    <thead className='ranking-header'>
                        <tr>
                            <th className='ranking-header-cell'>Rank</th>
                            <th className='ranking-header-cell'>Pok√©mon</th>
                            <th className='ranking-header-cell'>Change</th>
                        </tr>
                    </thead>
                    <tbody className='ranking-body'>
                        {pokemonList.map((pokemon, i) => (
                            <tr key={pokemon.id}>
                                <td className='ranking-body-cell'>{i+1}.</td>
                                <td className='ranking-body-cell'>{pokemon.name}</td>
                                <td className='ranking-body-cell'></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}
