import React from 'react';

export default class RankingTable extends React.Component {
    componentDidMount() {
		(async () => {
			let json = await fetch("/ranking");
            let result = await json.json();
            let sorted = this.getRankedList('hp', result);
            console.log(sorted);
		})();
	}

    getRankedList(stat, list) {
        let result = list.sort((a, b) => {
            return b[stat] - a[stat]
        });

        return result;
    }

    compareStats(stat, pokemon1, pokemon2) {
        return pokemon2[stat] - pokemon1[stat];
    }

    render() {
        return (
            <div>
                <h1>Community Rankings</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Rank</th>
                            <th>Pok√©mon</th>
                            <th>Change</th>
                        </tr>
                    </thead>
                </table>
            </div>
        );
    }
}
