import React from 'react';

export default class RankingTable extends React.Component {
    constructor(props) {
		super(props);

        this.state = {
            pokemonList: [{
                name: "Loading...",
                hp: 0,
                attack: 0,
                defense: 0,
                special_attack: 0,
                special_defense: 0,
                speed: 0
            }]
        };
	}

    componentDidMount() {
		(async () => {
			let json = await fetch("/ranking");
            let result = await json.json();
            let ranked = this.getRankedList('hp', result);
            
            this.setState({
                pokemonList: ranked
            });
		})();
	}

    getRankedList(stat, list) {
        let result = list.sort((a, b) => {
            return b[stat] - a[stat]
        });

        return result;
    }

    render() {
        let pokemonList = this.state.pokemonList;

        return (
            <div>
                <h1>Community Rankings</h1>
                <table className='ranking-table'>
                    <thead>
                        {/* <tr>
                            <th className='ranking-header'>Rank</th>
                            <th className='ranking-header'>Pok√©mon</th>
                            <th className='ranking-header'>Change</th>
                        </tr> */}
                    </thead>
                    <tbody>
                        {/* {pokemonList.map((pokemon, i) => (
                            <tr key={pokemon.id}>
                                <td className='ranking-body'>{i+1}</td>
                                <td className='ranking-body'>{pokemon.name}</td>
                                <td className='ranking-body'></td>
                            </tr>
                        ))} */}
                    </tbody>
                </table>
            </div>
        );
    }
}
